// dashboard/error.js
import { useEffect } from 'react';

export default function Error({ error, reset }) {
  useEffect(() => {
    console.error(error);
  }, [error]);

  return (
    <div>
      <h2>Something went wrong!</h2>
      <button
        onClick={
          // \u5C1D\u8BD5\u6062\u590D
          () => reset()
        }
      >
        Try again
      </button>
    </div>
  );
}
