  // Do something with event.data
  console.log('Received message:', event.data);

  // \u53EF\u4EE5\u68C0\u67E5event.origin\u6765\u9A8C\u8BC1\u6D88\u606F\u53D1\u9001\u8005\u7684\u6E90
