const express = require('express');
const app = express();

app.get('/long-polling-endpoint', (req, res) => {
  // \u6A21\u62DF\u5EF6\u8FDF
  setTimeout(() => {
    res.json({ message: 'Hello Client' });
  }, 1000);
});

app.listen(3000, () => console.log('Server is running on port 3000'));
