// lib.js(CommonJS)
var counter = 3;
function incCounter() {
  counter++;
}
module.exports = {
  counter: counter,
  incCounter: incCounter,
};

// main.js
var mod = require('./lib');

console.log(mod.counter); // 3
mod.incCounter();
console.log(mod.counter); // 3

// ES Module
// m1.js(ES Module)
export var foo = 'bar';
setTimeout(() => (foo = 'baz'), 500);

// m2.js(ES Module)
import { foo } from './m1.js';
console.log(foo); // bar
setTimeout(() => console.log(foo), 500); //baz

