const isValid = (str) => {
  if (!str) {
    return true;
  }
  const stack = [];
  const arr = str.split('');
  for (const item of arr) {
    if (['(', '[', '{'].includes(item)) {
      stack.push(map[item]);
    } else if (!stack.length || stack.pop() !== item) {
      return false;
    }
  }
  return !stack.length;
};

