const data = ['\u9605\u8BFB', '\u5199\u4F5C', '\u51A5\u60F3'];

export async function GET() {
  return NextResponse.json({ data });
}

export async function POST(request) {
  const formData = await request.formData();
  const todo = formData.get('todo');
  data.push(todo);
  return NextResponse.json({ data });
}
