export async function setTaskDone(id: number) {
  const user = await currentUser();

  if (!user) {
    throw new Error('\u7528\u6237\u672A\u767B\u5F55\uFF0C\u8BF7\u5148\u767B\u5F55');
  }

  await prisma.task.update({
    where: {
      id: id,
      userId: user.id,
    },
    data: {
      done: true,
    },
  });

  revalidatePath('/');
}
