  const stream = renderToPipeableStream(<App />, {
    onAllReady() {
      // \u5F53\u6240\u6709\u7EC4\u4EF6\u90FD\u51C6\u5907\u597D\u65F6\u8C03\u7528
      res.statusCode = 200; // \u8BBE\u7F6EHTTP\u72B6\u6001\u7801
      stream.pipe(res); // \u5C06\u6E32\u67D3\u7684\u7EC4\u4EF6\u6D41\u5F0F\u4F20\u8F93\u5230\u54CD\u5E94\u4E2D
    },
    onError(error) {
      // \u6E32\u67D3\u8FC7\u7A0B\u4E2D\u53D1\u751F\u9519\u8BEF\u65F6\u8C03\u7528
      console.error(error);
      res.statusCode = 500;
      res.send('Internal Server Error');
    },
  });
